def binary_search(arr: list, target: int) -> int:
    """
    Sıralı bir listede binary search uygular.
    Bulursa index, bulamazsa -1 döner.
    """
    left, right = 0, len(arr) - 1

    while left <= right:
        mid = (left + right) // 2

        if arr[mid] == target:
            return mid
        elif arr[mid] < target:
            left = mid + 1
        else:
            right = mid - 1

    return -1

def factorial(num):
    if num==0:
        return 1
    else:
        return num * factorial(num - 1)

def fibonacci(n: int) -> list:
    """
    n elemanlı Fibonacci serisini döner.
    """
    if n <= 0:
        return []

    if n == 1:
        return [0]

    sequence = [0, 1]

    while len(sequence) < n:
        sequence.append(sequence[-1] + sequence[-2])

    return sequence

def is_prime(n: int) -> bool:
    if n < 2:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True
